import React, { Component } from 'react'
import { Text, View, StyleSheet, TextInput, Alert} from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler'
import firebase from 'firebase'
import { Path, Svg } from 'react-native-svg';
import { Ionicons } from '@expo/vector-icons';

class Login extends Component {

    state={
        email:'',
        password:'',
        loading:false
    }

    onBottomPress = () => {
        firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.password)
        .then(this.onLoginSuccess)
        .catch(err => {
            Alert.alert('Error de autenticación', 'El email o contraseña introducidos no son correctos')
        })

    
    }
    onLoginSuccess =  () =>{
        this.setState({
            error:'',
            loading:false
        })
    }


    render() {
        return (
            <View style={styles.container}>
                <Svg width="100%" height="45%" style={{right: 100}}>
                    <Text style={styles.logo}>Blitz</Text>
                    <Path fill="#FFFFFF" fill-opacity="5" d="M0,32L17.1,64C34.3,96,69,160,103,192C137.1,224,171,224,206,202.7C240,181,274,139,309,149.3C342.9,160,377,224,411,213.3C445.7,203,480,117,514,101.3C548.6,85,583,139,617,144C651.4,149,686,107,720,90.7C754.3,75,789,85,823,117.3C857.1,149,891,203,926,224C960,245,994,235,1029,245.3C1062.9,256,1097,288,1131,256C1165.7,224,1200,128,1234,90.7C1268.6,53,1303,75,1337,106.7C1371.4,139,1406,181,1423,202.7L1440,224L1440,0L1422.9,0C1405.7,0,1371,0,1337,0C1302.9,0,1269,0,1234,0C1200,0,1166,0,1131,0C1097.1,0,1063,0,1029,0C994.3,0,960,0,926,0C891.4,0,857,0,823,0C788.6,0,754,0,720,0C685.7,0,651,0,617,0C582.9,0,549,0,514,0C480,0,446,0,411,0C377.1,0,343,0,309,0C274.3,0,240,0,206,0C171.4,0,137,0,103,0C68.6,0,34,0,17,0L0,0Z"></Path>
                </Svg>

                <Text style={styles.regularText}>Bienvenido,</Text>
                <Text style={[styles.regularText, {fontSize: 15, color: '#D2D6E7'}]}>Inicia sesión para acceder a tu cuenta</Text>

                <View style={styles.inputContainer}>
                    <View style={styles.mailContainer}>
                        <Ionicons style={[styles.formIcons]} name={'ios-mail'} color={'white'} size={30} />
                        <TextInput placeholder="Introduce tu email" style={styles.input} value={this.state.email} onChangeText={ email => this.setState({email}) } />
                    </View>
                    <View style={styles.mailContainer}>
                        <Ionicons style={{paddingRight: 15}} name={'ios-lock'} color={'white'} size={30} />
                        <TextInput placeholder="Contraseña" style={styles.input} secureTextEntry value={this.state.password} onChangeText={password => this.setState({password})} />
                    </View>
                </View>

                <View style={styles.buttonContainer}>
                    <TouchableOpacity activeOpacity={0.9} style={styles.button} onPress={this.onBottomPress} >
                        <Text style={styles.buttonText}>Acceder</Text>
                    </TouchableOpacity>
                 </View>
                 

            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#2450E7',
    },
    inputContainer: {
        padding: 20
    },
    mailContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20
    },
    formIcons: {
        paddingRight: 10
    },
    input: {
        color: 'white',
        height: 50,
        borderColor: 'white',
        borderWidth: 1.5,
        borderRadius: 5,
        paddingLeft: 10,
        fontSize: 15,
        flex: 1
    },
    buttonContainer: {
        paddingHorizontal: 20
    },
    buttonText:{
        textAlign: 'center',
        color: '#2450E7',
        fontFamily: 'Bold',
        fontSize: 20
    },
    button:{
        padding: 15,
        backgroundColor: 'white',
        borderRadius: 50
    },
    logo: {
        color: 'white',
        fontFamily: 'Bold',
        letterSpacing: 1,
        fontSize: 40,
        alignSelf: 'center',
    },
    regularText: {
        color: 'white',
        fontFamily: 'Regular',
        fontSize: 30,
        alignSelf: 'center',
    }
});

export default Login;
