import * as React from 'react';
import { Text, View, Button, StyleSheet } from 'react-native';
import { Categoria } from '../Components'

const categorias = [
  {icon: 'ios-fitness', title: 'Gimnasio', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Cocina', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Música', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Fotografía', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Literatura', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Arte', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Ciencias', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Coding', color: '#1652f0'},
  {icon: 'ios-fitness', title: 'Gaming', color: '#1652f0'},
]

const Home = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <View style={styles.categoryContainer}>
        {categorias.map(({ icon, title, color }) => (
          <Categoria icon={icon} title={title} color={color}/>
        ))}
      </View>
    </View>
  );
};

export default Home;

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  categoryContainer: {
    alignContent: 'center',
    alignItems: 'center',
    flexDirection: "column",
    padding: 20
  }
});
